mapper.update:
UPDATE narcissus.smart_house_mapping nshm
SET nshm.`status` = '1',nshm.threeID=#{threeId}
WHERE
nshm.provider_code = #{providerCode}
AND nshm.house_catalog = #{houseCatalog}
AND nshm.data_type =#{dataType}
AND nshm.h2omeID = #{h2omeId}

UPDATE narcissus.smart_house_mapping nshm
SET nshm.`status` = '2'
WHERE
nshm.provider_code = #{providerCode}
AND nshm.house_catalog = #{houseCatalog}
AND nshm.data_type =#{dataType}
AND nshm.h2omeID = #{h2omeId}
AND nshm.threeID=#{threeId}

UPDATE narcissus.smart_device_v2
SET manage_status = 1
WHERE
room_ID= #{roomId}
AND house_catalog = #{type} AND provider_code=#{providerCode}

UPDATE narcissus.smart_device_v2
SET manage_status = 1
WHERE
public_zone_ID = #{publicZoneId}
AND house_catalog =#{type}  AND provider_code=#{providerCode}

UPDATE narcissus_test.smart_lock_password
pwd_user_name = #{userName}, password = #{password}, send_to_mobile = #{mobile}, description = #{remark}, valid_time_start = #{enableTime}, valid_time_end = #{disableTime},
WHERE smart_lock_password_ID=#{id}

UPDATE narcissus_test.smart_lock_password
pwd_user_name = #{userName}, password = #{password}, send_to_mobile = #{mobile}, description = #{remark}, valid_time_start = #{enableTime}, valid_time_end = #{disableTime},
WHERE and threeID=#{uuid}

UPDATE narcissus_test.smart_gateway_bind_v2 ntsgbv2
  SET ntsgbv2.smart_gateway_ID=#{smartGatewayId}
where ntsgbv2.smart_device_ID=#{innerLockId}

mapper.select:
SELECT * FROM caspain_test.room_contract c
WHERE c.`status` = 'active' AND c.signed IN (0,2) AND c.is_delete = 0
AND c.room_id = #{roomId}

SELECT * FROM caspain_test.room_rentorder r
WHERE r.is_delete = 0 AND r.pay_status = 0 AND r.contract_id = #{contractId}
ORDER BY r.created_at LIMIT 1

SELECT * FROM `volga_test`.room_contract c
WHERE c.`status` = 'active' AND c.signed IN (0,2) AND c.is_delete = 0
AND c.room_id = #{roomId}

SELECT * FROM `volga_test`.room_rentorder r
WHERE r.is_delete = 0 AND r.pay_status = 0 AND r.contract_id = #{contractId}
ORDER BY r.created_at LIMIT 1
